import React from 'react'
import "bootstrap/dist/css/bootstrap.min.css";
import { Card } from "react-bootstrap";

const UseCases = () => {
    const UseCaseCard = ({title,description }) => {
        return (
          <Card className={`shadow-lg text-black w-100 h-100`}style={{ minHeight: '300px', minWidth:'200px'}}> 
            <Card.Body>
              <Card.Title className="text-primary">{title}</Card.Title>
              {/* <Card.Subtitle className="mb-2 text-muted">{subtitle}</Card.Subtitle> */}
              <Card.Text className="text-muted mt-2">{description}</Card.Text>
            </Card.Body>
          </Card>
        );
      };

      // Main Layout Component
      const Cards = () => {
        const cards = [
         {title: "Living Alone, Staying safe", description:"Whether you're a student, professional, or senior living alone, our web ensures someone checks in on you daily. If anything happens, your emergency contact gets notified instantly."}, 
         {title: "Daily Activities", description:"Your day-to-day activities shouldn never feel dangerous or risky. Focus on your responsibilities—we’ve got your safety covered"}, 
         {title: "Adventurers & Solo Travelers", description:"Love hiking, traveling, or exploring on your own? Set a check-in time before your trip. If you don't respond, your trusted contact will be alerted, ensuring your safety no matter where you are."}, 
         {title: "Night Shifts & Late Walks", description:"Walking home alone after work or a night out? Set a quick check-in, and if you don’t confirm your safety, an alert is sent. It’s simple peace of mind in just a few taps."}, 
        ];

        return (
          <div className="cards-container">
            <div className="container mt-4">
              <div className="row g-4 justify-content-center">
                {cards.map((card, index) => (
                  <div key={index} className="col-12 col-sm-6 col-lg-3">
                    <UseCaseCard {...card} />
                  </div>
                ))}
              </div>
            </div>
          </div>

        );
      }

}

export default UseCases
